$(function () {
    $('.dropdown-items a.active').parent().prevAll().find('a').addClass('siblings');

    //
    jQuery(document).on('click', '.open-menu', function (){
        var $navbarItem = $('.navbar-item');
        if ($navbarItem.css('margin-left') == '0px') {
            $navbarItem.css('margin-left', '-240px');
            $('.overlay').css('display', 'none');
            $("html, body").removeAttr("style");
        } else {
            $navbarItem.css('margin-left', '0px');
            $('.overlay').css('display', 'block');
            $("html").css('overflow', 'hidden');
        }
    });

    //
    tinymce.init({
        selector: '.textEditor',
        toolbar: 'styles | bold italic underline | alignleft aligncenter alignright alignjustify | code',
        plugins: "code",
        menubar: '',
        height: 200
    });

    //
    $(".data-select").select2({
        placeholder: ""
    });

})